import React, { Component } from 'react'
import Outcomes from './Outcomes';

export class App extends Component {
    state = {step: 1,
            learning_outcomes:[]};

    //proceed to next step
    nextStep =()=>{
        const {step} = this.state;
        this.setState({step: step+1});
    }
    //go back to previous step
    backStep =()=>{
        const {step} = this.state;
        this.setState({step: step-1});
    }
    render() {
        return (
            <div>
                <Outcomes/>
            </div>
        )
    }
}

export default App;
