import React, { Component } from 'react'
import Outcomes from './Outcomes';

export class App extends Component {
    state = {step: 1,
            outcomeList:[]};

    //proceed to next step
    nextStep =()=>{
        const {step} = this.state;
        this.setState({step: step+1});
    };
    //go back to previous step
    backStep =()=>{
        const {step} = this.state;
        this.setState({step: step-1});
    };
    //handle outcome change
    handleOutcomeChange = (outcome)=>{
        const {outcomeList} = this.state;
        this.setState({outcomeList:[...outcomeList,outcome]});
    };
    render() {
        const {step} = this.state;
        const {outcomeList} = this.state;
        switch(step){
            case 0:
                return (
                    <Outcomes outcomes = {this.outcomeList} handleChange = {this.handleOutcomeChange}/>
                );
            default:
                return(
                    <div>
                        welcome page
                    </div>
                );

        }
    }
}

export default App;
