import React, { Component } from 'react';
import Outcomes from './Outcomes';

export class App extends Component {
	state = { outcomeList: [] };

	//handle outcome change
	handleOutcomeChange = (outcome) => {
		const { outcomeList } = this.state;
		const newList = [...outcomeList, outcome];
		this.setState({ outcomeList: newList });
	};
	render() {
		return (
			<Outcomes
				outcomes={this.state.outcomeList}
				handleChange={this.handleOutcomeChange}
			/>
		);
	}
}

export default App;
